m=1; % carrier
n=2; % fundamental
ma=0:0.01:1;
ma=0.8;
mag_f=ma/2;
mag_fsw=2*m*besselj(0,ma*pi/2)/pi;
mag_side=abs(2.*besselj(n,m*ma*pi/2)/(m*pi));
%%
Vsc=[];
Vsr=[];

Delta_phase_carrier_a=0:pi/96:2*pi;
Delta_phase_reference_a=0:pi/96:2*pi;

for Delta_phase_carrier=Delta_phase_carrier_a
    Vsc_a=[];
    Vsr_a=[];
    for Delta_phase_reference=Delta_phase_reference_a
        Vsc_a=[Vsc_a, sqrt(2)*mag_fsw*sqrt(1-cos(Delta_phase_carrier))];
        Vsr_a=[ Vsr_a sqrt(2)*mag_f*sqrt(1-cos(Delta_phase_reference))];
    end
    
    
    Vsc=[Vsc; Vsc_a];
    Vsr=[Vsr; Vsr_a];
  
end
